import React, { Component } from "react";

export default class Mp3Button extends Component {
  constructor(props) {
    super(props);
    var buttonTitle = this.props.mp3;
    buttonTitle = buttonTitle.replaceAll("_", " ").rplace(".mp3", "");
    this.state = {
      buttonTitle: buttonTitle,
      buttonClass: "unclicked-mp3-button"
    };
  }
  componentWillReceiveProps(nextProps) {
    if (nextProps.clickedButtonId === nextProps.mp3) {
      this.setState(state => ({
        buttonClass: "clicked"
      }));
    } else {
      this.setState(state => ({
        buttonClass: "unclicked"
      }));
    }
  }
  render() {
    return (
      <div>
        <button id={this.props.mp3} className={this.state.buttonClass}>
          {this.state.buttonTitle}
        </button>
      </div>
    );
  }
}
