import React, { Component } from "react";
import ReactDOM from "react-dom";
import { PlayButton, Timer } from "react-soundplayer/components";

// it's just an alias for `withSoundCloudAudio` but makes code clearer
import { withCustomAudio } from "react-soundplayer/addons";

const streamUrl = "https://example.org/path/to/file.mp3";

// some track meta information
const trackTitle = "Great song by random artist";

const AWSSoundPlayer = withCustomAudio(props => {
  const { trackTitle } = props;

  return (
    <div>
      <PlayButton {...this.props} />
      <h2>{trackTitle}</h2>
      <Timer {...this.props} />
    </div>
  );
});
let playlist = [
  { src: "/audio/CASE.mp3", title: "CASE", artist: "Lea Hughes" },
  { src: "/audio/BRIDGE.mp3", title: "Bridge", artist: "Lea Hughes" }
];

export default AWSSoundPlayer;
