// import AudioPlayer from "react-modular-audio-player";
import React, { Component } from "react";
import ReactAudioPlayer from "react-audio-player";
//...

let playlist = [
  { src: "/audio/CASE.mp3", title: "CASE", artist: "Lea Hughes" },
  { src: "/audio/BRIDGE.mp3", title: "Bridge", artist: "Lea Hughes" }
];

export default class Listen extends Component {
  render() {
    const Player = () => (
      <ReactAudioPlayer
        src="./audio/BRIDGE.mp3"
        autoPlay
        onPlay={e => console.log("onPlay")}
      />
    );

    const style = {
      marginTop: "150px"
    };

    return (
      <div style={style}>
        <div>{Player}</div>
        <h2>Yes I, the H2 is working</h2>
      </div>
    );
  }
}
